var ob = require('./../../');

var a = ob.Molecule.fromJSON(
    {
        "atoms": [{
            "index": 1,
            "atomicNumber": 6,
            "symbol": "C",
            "position": {"x": 0.8735494401409668, "y": 1.8148742552654058, "z": -1.4674736330911466}
        }, {
            "index": 2,
            "atomicNumber": 6,
            "symbol": "C",
            "position": {"x": 0.43864938058069725, "y": -0.008123136620963178, "z": -1.9763607007808242}
        }], "bonds": [{"begin": 1, "end": 2, "order": 1}]
    }
);


var conversion = new ob.Conversion();
var str = conversion.setOutFormat('mol2').setInFormat('mol2').write(a);

a = conversion.read(str);
a.addHydrogens();


